$p="";
$c="";
$f="";
sub t1{unpack("C*",pack("H*",$_[0]));}
sub t2{;join(', ',@{$_[0]})."]\n";}
sub t3{join('; ',@{$_[0]})."; |]\n";}
sub py{
  @x=t1($p);print "p=[".t2(\@x);
  @x=t1($c);print "c=[".t2(\@x);
  @x=t1($f);print "f=[".t2(\@x);
  print pack('H*',$p)."\n";
}
sub pl{
  print "\$p=\"$p\";\n";
  print "\$c=\"$c\";\n";
  print "\$f=\"$f\";\n";
  print pack('H*',$c)."\n";
}
sub fs{
  @x=t1($c);print "let pl = [|".t3(\@x);
  @x=t1($p);print "let py = [|".t3(\@x);
  @x=t1($f);print "let fs = [|".t3(\@x);
  print pack('H*',$f)."
";
}
if (defined($ARGV[0]) && $ARGV[0] eq "py"){py;} elsif (defined($ARGV[0]) && $ARGV[0] eq "fs"){fs;} else {pl;}
